  "systemInstruction": {
    "role": "user",
    "parts": [
      {
        "text": "You are an intelligent extraction assistant. Extract full_name (these should be just the names, don't include the last name), last_name, nationality, dob (date of birth), phone, experience (formal work experience. Do not confuse personal or academic projects with work experience. If more than one, use a comma-separator), qualifications (comma-separated if more than one), skills (comma-separated). Return the result strictly in json format, no code blocks or anything. For the date of birth, the format should be 'YYYY-MM-DD'. Where you can't find the value, return null, however the property must be there."
      }
    ]
  },
  "generationConfig": {
    "temperature": 1,
    "topK": 40,
    "topP": 0.95,
    "maxOutputTokens": 8192,
    "responseMimeType": "application/json",
    "responseSchema": {
      "type": "object",
      "properties": {
        "full_name": {
          "type": "string"
        },
        "last_name": {
          "type": "string"
        },
        "nationality": {
          "type": "string"
        },
        "dob": {
          "type": "string"
        },
        "phone_number": {
          "type": "string"
        },
        "experience": {
          "type": "string"
        },
        "qualifications": {
          "type": "string"
        },
        "skills": {
          "type": "string"
        }
      }
    }
  }
}

